def f(x):
    return x**3 - x - 4

def bisection(a, b, tol):
    if f(a) * f(b) >= 0:
        return None

    while (b - a) / 2 > tol:
        c = (a + b) / 2
        if f(c) == 0:
            return c
        elif f(c) * f(a) < 0:
