from collections import deque

def bfs(graph, start, end):
    queue = deque([(start, 0)])
    visited = set()

    while queue:
        _, depth = queue.popleft()

        if depth == len(graph):
            if end in visited:
                return [start] + [node for node in graph[start] if node != end]
            else:
